mapper.select:
select ,`name`,g.sicknum,g.doctor,g.survey_time,g.livenum from hzh_common common left join hzh_general g on (g.id = common.child_id)
where 1 = 1

  and common.low = #{common.low,jdbcType=INTEGER}


  and common.lowreason = #{common.lowreason,jdbcType=VARCHAR}


  and common.infect = #{common.infect,jdbcType=INTEGER}


  and common.infectreason = #{common.infectreason,jdbcType=VARCHAR}


  and common.stop = #{common.stop,jdbcType=INTEGER}


  and common.stopreason = #{common.stopreason,jdbcType=VARCHAR}


  and common.high = #{common.high,jdbcType=INTEGER}


  and common.highreason = #{common.highreason,jdbcType=VARCHAR}


  and common.around = #{common.around,jdbcType=INTEGER}


  and common.aroundreason = #{common.aroundreason,jdbcType=VARCHAR}


  and common.listen = #{common.listen,jdbcType=INTEGER}


  and common.is_audiphones = #{common.isAudiphones,jdbcType=INTEGER}


  and common.listenreason = #{common.listenreason,jdbcType=VARCHAR}


  and common.eye = #{common.eye,jdbcType=INTEGER}


  and common.is_glasses = #{common.isGlasses,jdbcType=INTEGER}


  and common.eyereason = #{common.eyereason,jdbcType=VARCHAR}


  and common.disease = #{common.disease,jdbcType=INTEGER}


  and common.diseasereason = #{common.diseasereason,jdbcType=VARCHAR}


  and common.lax = #{common.lax,jdbcType=INTEGER}


  and common.laxreason = #{common.laxreason,jdbcType=VARCHAR}


  and common.vaccin = #{common.vaccin,jdbcType=INTEGER}


  and common.vaccinreason = #{common.vaccinreason,jdbcType=VARCHAR}


  and common.child_id = #{common.childId,jdbcType=INTEGER}

select

  distinct


from hzh_common




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}
