mapper.select:
select

  distinct


from hzh_emotion




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select  from hzh_emotion e left join hzh_general g on (e.child_id = g.id) where 1=1

  and child_id = #{childId,jdbcType=INTEGER}


  and rela = #{rela,jdbcType=INTEGER}


  and imitate = #{imitate,jdbcType=INTEGER}


  and emotion = #{emotion,jdbcType=INTEGER}


  and motion = #{motion,jdbcType=INTEGER}


  and nonlife = #{nonlife,jdbcType=INTEGER}


  and `change` = #{change,jdbcType=INTEGER}


  and vision = #{vision,jdbcType=INTEGER}


  and hearing = #{hearing,jdbcType=INTEGER}


  and nearby = #{nearby,jdbcType=INTEGER}


  and anxious = #{anxious,jdbcType=INTEGER}


  and `language` = #{language,jdbcType=INTEGER}


  and nolan = #{nolan,jdbcType=INTEGER}


  and activity = #{activity,jdbcType=INTEGER}


  and intelligence = #{intelligence,jdbcType=INTEGER}


  and impression = #{impression,jdbcType=INTEGER}


  and frasum = #{frasum,jdbcType=INTEGER}


  and addtime = #{addtime,jdbcType=INTEGER}
