mapper.select:
select

  distinct


from hzh_nerve




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select ,`name`,g.sicknum from hzh_nerve n left join hzh_general g on (g.id = n.child_id)
where 1=1

  and g.name like concat('%','${hzhGeneral.name}','%')


  and n.child_id = #{hzhNerve.childId}


  and yes_no = #{hzhNerve.yesNo,jdbcType=INTEGER}


  and epilepsy = #{hzhNerve.epilepsy,jdbcType=INTEGER}


  and epilepsyreason = #{hzhNerve.epilepsyreason,jdbcType=VARCHAR}


  and tension = #{hzhNerve.tension,jdbcType=INTEGER}


  and tensionreason = #{hzhNerve.tensionreason,jdbcType=VARCHAR}


  and imbalance = #{hzhNerve.imbalance,jdbcType=INTEGER}


  and imbalancereason = #{hzhNerve.imbalancereason,jdbcType=VARCHAR}


  and gait = #{hzhNerve.gait,jdbcType=INTEGER}


  and gaitreason = #{hzhNerve.gaitreason,jdbcType=VARCHAR}


  and lncrease = #{hzhNerve.lncrease,jdbcType=INTEGER}


  and lncreasereason = #{hzhNerve.lncreasereason,jdbcType=VARCHAR}


  and weaken = #{hzhNerve.weaken,jdbcType=INTEGER}


  and weakenreason = #{hzhNerve.weakenreason,jdbcType=VARCHAR}


  and other = #{hzhNerve.other,jdbcType=INTEGER}


  and otherreason = #{hzhNerve.otherreason,jdbcType=VARCHAR}


  and child_id = #{hzhNerve.childId,jdbcType=INTEGER}
